import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios'

function App() {

const[ticket, setticket] = useState('')
const[movieReview, setMovieReview] = useState('')
const [movieReviewList, setMovieReviewList] = useState([])
const [newReview, setNewReview] = useState('')


useEffect(()=>{
  Axios.get('http://localhost:3001/api/get').then((response)=>{
setMovieReviewList(response.data)
  })
}, [])

const submitReview= () => {
 Axios.post('http://localhost:3001/api/insert', {
   ticket: ticket, 
   movieReview:movieReview
  });
  setMovieReviewList([...movieReviewList, {ticket:ticket, movieReview:movieReview}]);
}

const deleteReview = (movie) =>{
  Axios.delete(`http://localhost:3001/api/delete/${movie}`)
}
const updateReview = (movie) =>{
  Axios.put(`http://localhost:3001/api/update/`, {
    ticket:movie,
    movieReview:newReview
  })
  setNewReview('')
}

  return (
    <div className="App">
      <h1>CRUD APPLI</h1>
      <div className="form">
      <label>Movie Name</label>
      <input type='text' name="ticket" onChange={(e)=>{
        setticket(e.target.value)
      }}/>
      <label>Movie Review</label>
      <input type='text' name="movieReview" onChange={(e)=>{
        setMovieReview(e.target.value)
      }}/>
      <button onClick={submitReview}>Submit</button>
      {movieReviewList.map((val)=>{
        return (
        <div className="card">
          <h1>{val.Numero_de_ticket}</h1>
          <p>{val.Descritpif_du_ticket}</p>

          </div>
        )

      })}
      </div>
    </div>
  );
}

export default App;
