import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios'
import './style.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal } from './component/Modal';

function App() {

const[ticket, setticket] = useState('')
const[description, setdescription] = useState('')
const[date, setDate] = useState('')
const[duree, setDuree] = useState('')
const[action, setAction] = useState('')
const [descriptionList, setdescriptionList] = useState([])
const [newReview, setNewReview] = useState('')


let sum = descriptionList.reduce(function(prev, current) {
  return prev + +current.Duree_en_heure
}, 0);

useEffect(()=>{

  Axios.get('http://192.168.0.186:3001/api/get').then((response)=>{
setdescriptionList(response.data)

})

  
}, [])

const submitReview= () => {
 Axios.post('http://192.168.0.186:3001/api/insert', {
   ticket: ticket, 
   description:description,
   date:date,
   duree:duree,
   action:action
  });
  setdescriptionList([...descriptionList, {Numero_de_ticket:ticket, Descritpif_du_ticket:description, Date_appel:date, Duree_en_heure:duree, Action:action}]);
  setticket('')
  setdescription('')
  setDate('')
  setDuree('')
  setAction('')
  Array.from(document.querySelectorAll("input")).forEach(
    input => (input.value = "")
  );
}

const deleteReview = (ticket) =>{
  Axios.delete(`http://192.168.0.186:3001/api/delete/${ticket}`)
  setdescriptionList(descriptionList.filter(review => review.Numero_de_ticket !== ticket))
}
const updateReview = (movie) =>{
  Axios.put(`http://192.168.0.186:3001/api/update/`, {
    ticket:movie,
    description:newReview
  })
  setNewReview('')
}

const handleModal = () =>{
var modal = document.getElementById("myModal");
var span = document.getElementsByClassName("close")[0];
modal.style.display = "block";
// When the user clicks on <span> (x), close the modal
span.onclick = function() {
  modal.style.display = "none";
}
// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
  if (event.target == modal) {
    modal.style.display = "none";
  }
}
}


  return (
    <div className="App">
      <h1>Astreinte</h1>
      <div id="form">
      <label>Ticket</label>
      <input type='text' name="ticket" onChange={(e)=>{
        setticket(e.target.value)
      }}/>
      <label>Description</label>
      <input type='textarea' rows="3" cols="33" name="description" onChange={(e)=>{
        setdescription(e.target.value)
      }}/>
      <label>Date</label>
      <input type='date' name="date" onChange={(e)=>{
        setDate(e.target.value)
      }}/>
      <label>Dur√©e</label>
            <input type='text' name="duree" onChange={(e)=>{
        setDuree(e.target.value)
      }}/>
      <label>Action</label>

            <input type='text' name="action" onChange={(e)=>{
        setAction(e.target.value)
      }}/>
      <button onClick={submitReview}>Submit</button>
      <div>
      <button id="myBtn" onclick={handleModal}>Open Modal</button>
      <Modal id="myModal" />
      Totale heures supp : {sum}
      </div>

      <div className="container py-5">
    <div className="row">
        <div className="col-lg-12 mx-auto">
            <div className="card rounded-0 border-0 shadow">
                <div className="card-body">      
                
                    <div className="table-responsive">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th scope="col">Ticket</th>
                                    <th scope="col">Date</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Action</th>
                                    <th scope="col">Duree</th>
                                    <th scope="col">Delete</th>
                                    <th scope="col"></th>
                                </tr>
                            </thead>
                            <tbody>
                              {
                              descriptionList.map((val, index)=> {
                                var date = new Date(val.Date_appel)
                                return (
                                  
                                    <tr key={index}>
                                    <th scope="row">{val.Numero_de_ticket}</th>
                                    <td>{date.toLocaleDateString()}</td>
                                    <td className='Description'>{val.Descritpif_du_ticket}</td>
                                    <td>{val.Action}</td>
                                    <td>{val.Duree_en_heure}h</td>
                                    <td><button onClick={()=>{deleteReview(val.Numero_de_ticket)}}>Delete</button></td>
                                    <td></td>
                                </tr>
                                )
                              })}

                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
      </div>
    </div>
  );
}

export default App;
