import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios'
import './style.css'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {

const[ticket, setticket] = useState('')
const[description, setdescription] = useState('')
const[date, setDate] = useState('')
const[duree, setDuree] = useState('')
const[action, setAction] = useState('')

const [descriptionList, setdescriptionList] = useState([])
const [newReview, setNewReview] = useState('')


useEffect(()=>{
  Axios.get('http://localhost:3001/api/get').then((response)=>{
setdescriptionList(response.data)
  })
}, [])

const submitReview= () => {
 Axios.post('http://localhost:3001/api/insert', {
   ticket: ticket, 
   description:description,
   date:date,
   duree:duree,
   action:action
  });
  setdescriptionList([...descriptionList, {ticket:ticket, description:description}]);
}
 
const deleteReview = (movie) =>{
  Axios.delete(`http://localhost:3001/api/delete/${movie}`)
}
const updateReview = (movie) =>{
  Axios.put(`http://localhost:3001/api/update/`, {
    ticket:movie,
    description:newReview
  })
  setNewReview('')
}

  return (
    <div className="App">
      <h1>CRUD APPLI</h1>
      <div id="form">
      <label>Ticket</label>
      <input type='text' name="ticket" onChange={(e)=>{
        setticket(e.target.value)
      }}/>
      <label>Description</label>
      <input type='textarea' rows="3" cols="33" name="description" onChange={(e)=>{
        setdescription(e.target.value)
      }}/>
      <label>Date</label>
      <input type='date' name="date" onChange={(e)=>{
        setDate(e.target.value)
      }}/>
      <label>Durée</label>
            <input type='text' name="duree" onChange={(e)=>{
        setDuree(e.target.value)
      }}/>
      <label>Action</label>

            <input type='text' name="action" onChange={(e)=>{
        setAction(e.target.value)
      }}/>
      <button onClick={submitReview}>Submit</button>
      <div class="container py-5">
    <div class="row">
        <div class="col-lg-7 mx-auto">
            <div class="card rounded-0 border-0 shadow">
                <div class="card-body p-5">
                    <div class="table-responsive">
                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">Numero du ticket</th>
                                    <th scope="col">Date</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Action</th>
                                    <th scope="col"></th>
                                </tr>
                            </thead>
                            <tbody>
                              {descriptionList.map((val)=> {
                                var date = new Date(val.Date_appel)
                                return (
                                    <tr>
                                    <th scope="row">{val.Numero_de_ticket}</th>
                                    <td>{date.getTime()}</td>
                                    <td>{val.Descritpif_du_ticket}</td>
                                    <td>{val.Action}</td>
                                    <td></td>
                                </tr>
                                )
                              })}

                            </tbody>
                        </table>
                    </div>
                    <a class="btn btn-primary rounded-0 btn-block" id="insertRow" href="#">Add new row</a>
                </div>
            </div>
        </div>
    </div>
</div>
      </div>
    </div>
  );
}

export default App;
