          date:result1.toLocaleDateString().replace(/\//g, '-').split('-').reverse().join('-'),
          duree:"1",
          action:"Check Loreal"
         })
         Axios.post('http://192.168.0.186:3001/api/insert', {
          ticket: "CheckLorealdu" + result2.toLocaleDateString().replace(/\//g, '-').split('-').join('-'), 
